// import logo from './logo.svg';
import '../index.css';
import './App.js';
import Header from './Header';
import Main from '../Main';
import Footer from './Footer';
import PopupWithForm from './PopupWithForm';
import React from 'react';

function App() {

  // const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false);

  function handleEditAvatarClick() {
    document.querySelector('.popup_profile-avatar')
    .classList.add("popup_opened");
    console.log('avatar')
 };
//  function handleEditProfileClick() {
//   setIsEditProfilePopupOpen.classList.add("popup_opened")
//   console.log('info')
// };
 function handleEditProfileClick() {
    document.querySelector('.popup_type_more-info')
    .classList.add("popup_opened");
    console.log('info')
 };
 function handleAddPlaceClick() {
    document.querySelector('.popup_type_more-item')
    .classList.add("popup_opened");
    console.log('item-add')
 }
  return (
    <div>
      <body className="page">
        <Header />
        <Main
          onEditProfile={handleEditProfileClick}
          onAddPlace={handleAddPlaceClick}
          onEditAvatar={handleEditAvatarClick}
        />
        <PopupWithForm
          container="_info"
          name="more-info"
          title="Редактировать профиль"
          submit="Сохранить"
        >
          <input required minlength="2" maxlength="40" name="inputName" id="formName" 
            className="form__input popup__input-name popup__input-name_info" placeholder="Жак-Ив Кусто"/>
          <span id="error-formName" className="form-name-error form__input-error"></span>
          <input id="formClass" required minlength="2" name="inputInfo" maxlength="200" placeholder="Название"
            className="form__input popup__input-name popup__input-name_more_heading"/>
          <span id="error-formClass" className="form-class-error form__input-error"></span>
        </PopupWithForm>
        <PopupWithForm
          name="more-item"
          title="Новое место"
          close="popup__close"
          submit="Создать"
        >
          <input required minlength="2" maxlength="30" name="inputNameCard" id="nameImage" placeholder="Название"
            className="form__input popup__input-name popup__input-name_item" />
          <span id="error-nameImage" className="form-item-error form__input-error"></span>
          <input required id="formLink" type="url" name="inputLink" placeholder="Ссылка на картинку"
            className="form__input popup__input-name popup__input-name_more_link" />
          <span id="error-formLink" className="form-link-error form__input-error"></span>
        </PopupWithForm>
        {/* <PopupWithForm name='delete-card' title='Обновить аватар' close='popup__close' submit='Да' /> */}
        {/* <PopupWithForm name='profile-avatar' title='Вы уверены?' close='popup__close' submit='Сохранить' /> */}
        <Footer />
      </body>
    </div>
  );
}
export default App;
