// import logo from './logo.svg';
import '../index.css';
import './App.js';
import Header from './Header';
import Main from '../Main';
import Footer from './Footer';
import PopupWithForm from './PopupWithForm';
import React from 'react';

function App() {

  // const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false);
    // const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);
  // const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = React.useState(false);

  function handleEditAvatarClick() {
    // document.querySelector('.popup_profile-avatar')
    document.querySelector('.popup_type_profile-avatar')
    .classList.add("popup_opened");
    console.log('avatar')
 };
 function handleEditProfileClick() {
  setIsEditProfilePopupOpen(true)
  console.log('info')
};
//  function handleEditProfileClick() {
//     document.querySelector('.popup_type_more-info')
//     .classList.add("popup_opened");
//     console.log('info')
//  };
 function handleAddPlaceClick() {
    document.querySelector('.popup_type_more-item')
    .classList.add("popup_opened");
    console.log('item-add')
 }
  return (
    <div>
      <body className="page">
        <Header />
        <Main
          onEditProfile={handleEditProfileClick}
          onAddPlace={handleAddPlaceClick}
          onEditAvatar={handleEditAvatarClick}
        />
        <PopupWithForm name="more-info" title="Редактировать профиль" submit="Сохранить">
          <input required minlength="2" maxlength="40" name="inputName" id="formName" 
              className="form__input popup__input-name popup__input-name_info" placeholder="Жак-Ив Кусто"/>
          <span id="error-formName" className="form-name-error form__input-error"></span>
          <input id="formClass" required minlength="2" name="inputInfo" maxlength="200" placeholder="Название"
              className="form__input popup__input-name popup__input-name_more_heading"/>
          <span id="error-formClass" className="form-class-error form__input-error"></span>
        </PopupWithForm>

        <PopupWithForm name="more-item" title="Новое место" submit="Создать">
          <input required minlength="2" maxlength="30" name="inputNameCard" id="nameImage" placeholder="Название"
              className="form__input popup__input-name popup__input-name_item" />
          <span id="error-nameImage" className="form-item-error form__input-error"></span>
          <input required id="formLink" type="url" name="inputLink" placeholder="Ссылка на картинку"
              className="form__input popup__input-name popup__input-name_more_link" />
          <span id="error-formLink" className="form-link-error form__input-error"></span>
        </PopupWithForm>

        <PopupWithForm name='profile-avatar' title='Обновить аватар' close='popup__close' submit="Сохранить" >
          <input required id="formLinkAvatar" type="url" name="inputLinkAvatar" placeholder="Ссылка на аватар" className="form__input popup__input-name" />
          <span id="error-formLinkAvatar" className="form-link-error form__input-error"></span>
          </PopupWithForm>

        {/* <PopupWithForm name="delete-card" title="Вы уверены?" submit='Да' >
        </PopupWithForm> */}

        <div className="popup popup_delete-card">
            <form className="popup__container popup__container-delete">
                <h3 className="popup__title popup__title_delete">Вы уверены?</h3>
                <button className="popup__button-submit popup__button-submit_delete">Да</button>
                <button type="reset" className="popup__close"></button>
        </form>
        </div>

        <Footer />
      </body>
    </div>
  );
}
export default App;
