import React from "react";
import avatar from './images/Avatar.svg'

function Main() {
    function handleEditAvatarClick() {
        document.querySelector('.popup_profile-avatar')
        .classList.add("popup_opened");
     };
     function handleEditProfileClick() {
        document.querySelector('.popup_type_more-info')
        .classList.add("popup_opened");
     };
     function handleAddPlaceClick() {
        document.querySelector('.popup_type_more-item')
        .classList.add("popup_opened");
     }

    return (
<main className="content">
  <section className="profile">
    <div className="profile__profile-avatar">
      <img onClick={handleEditAvatarClick} src={avatar} alt="Кусто" className="profile__avatar" />
    </div>
    <div className="profile__info">
      <h1 className="profile__title"></h1>
      <button onClick={handleEditProfileClick} type="button" className="profile__button-change"></button>
      <p className="profile__subtitle"></p>
    </div>
    <button onClick={handleAddPlaceClick} type="button" className="profile__button-add"></button>
  </section>
  <div className="elements">
    <template id="images-card" className="template-card card-template_type_default" >
      <div className="element">
        <img className="element__image  element__image_add" alt="Фото" />
        <h3 className="element__title"></h3>
        <button name="elementIconLike" type="button" className="element__icon"></button>
        <div name="elementLikes" className="element__likes"></div>
        <button type="button" className="element__delete"></button>
      </div>
    </template>
  </div>
  <div className="popup popup_type_more-image">
    <div className="popup__container-image">
      <img className="image" alt="Фото" />
      <button type="reset" className="popup__close"></button>
      <p className="popup__title-image"></p>
    </div>
  </div>
</main>
);

}



// handleEditAvatarClick()
