// import logo from './logo.svg';
import '../index.css';
import './App.js';
import Header from './Header';
import Main from './Main';
import Footer from './Footer';
import PopupWithForm from './PopupWithForm';
import React, { useEffect, useState } from 'react';
import Api from '../utils/api.js';
import { newApi } from '../utils/api.js';
import Card from './Card.js';
import ImagePopup from './ImagePopup.js';

function App() {
//   const [cards, setCards] = React.useState([]);
//   React.useEffect(() => {
//     newApi.getInitialCards()
//     .then((data) => {
//       setCards(
//         data.map((item) => ({
//           id: item.id,
//           link: item.link,
//           name: item.name,
//           likes: item.likes.length
//         }))
//       )
//     })
//     .catch((err) => {
//     console.log(err);
// });
//   })

const [cards, setCards] = useState();
useEffect(() => {
  newApi
    .getInitialCards()
    .then((data) => {
      console.log(setCards(data)
      )
    })
})

  const [userName, setUserName] = useState('')
  const [userDescription, setUserDescription] = useState('')
  const [userAvatar, setUserAvatar] = useState('')

  useEffect(() => {
    newApi
      .getUserInfo()
      .then((data) => {
        console.log(data.name);
        setUserName(data.name);
        setUserDescription(data.about);
        setUserAvatar(data.avatar);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);


  // const [profile, setProfile] = React.useState([]);
  // React.useEffect(() => {
  //   newApi.getUserInfo()
  //   .then((item) => {
  //     setProfile({
  //       userName: item.name,
  //       userDescription: item.about,
  //       userAvatar: item.avatar
  //   })
  //   })
  //   .catch((err) => {
  //     console.log(err);
  //   })
  // })

  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = React.useState(false);
  const [selectedCard, setSelectedCard] = React.useState(false);

  function closeAllPopups() {
    setIsEditProfilePopupOpen(false);
    setIsAddPlacePopupOpen(false);
    setIsEditAvatarPopupOpen(false);
    setSelectedCard(false);
  }

  function handleCardClick() {
    setSelectedCard(true)
  }

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true)
 };
 function handleEditProfileClick() {
  setIsEditProfilePopupOpen(true)
};
 function handleAddPlaceClick() {
  setIsAddPlacePopupOpen(true)
 }
  return (
    <div>
      <body className="page">
        <Header />
        <Main
          data={{userName, userDescription, userAvatar}}
          onEditProfile={handleEditProfileClick}
          onAddPlace={handleAddPlaceClick}
          onEditAvatar={handleEditAvatarClick}
          />
        <Card cards={cards} />

        <ImagePopup card={selectedCard} onClose={closeAllPopups} />

        <PopupWithForm onClose={closeAllPopups}   isOpen={isEditProfilePopupOpen} name="more-info" title="Редактировать профиль" submit="Сохранить">
          <input required minlength="2" maxlength="40" name="inputName" id="formName" 
              className="form__input popup__input-name popup__input-name_info" placeholder="Жак-Ив Кусто"/>
          <span id="error-formName" className="form-name-error form__input-error"></span>
          <input id="formClass" required minlength="2" name="inputInfo" maxlength="200" placeholder="Название"
              className="form__input popup__input-name popup__input-name_more_heading"/>
          <span id="error-formClass" className="form-class-error form__input-error"></span>
        </PopupWithForm>

        <PopupWithForm onClose={closeAllPopups} isOpen={isAddPlacePopupOpen} name="more-item" title="Новое место" submit="Создать">
          <input required minlength="2" maxlength="30" name="inputNameCard" id="nameImage" placeholder="Название"
              className="form__input popup__input-name popup__input-name_item" />
          <span id="error-nameImage" className="form-item-error form__input-error"></span>
          <input required id="formLink" type="url" name="inputLink" placeholder="Ссылка на картинку"
              className="form__input popup__input-name popup__input-name_more_link" />
          <span id="error-formLink" className="form-link-error form__input-error"></span>
        </PopupWithForm>

        <PopupWithForm onClose={closeAllPopups} isOpen={isEditAvatarPopupOpen} name='profile-avatar' title='Обновить аватар' close='popup__close' submit="Сохранить" >
          <input required id="formLinkAvatar" type="url" name="inputLinkAvatar" placeholder="Ссылка на аватар" className="form__input popup__input-name" />
          <span id="error-formLinkAvatar" className="form-link-error form__input-error"></span>
          </PopupWithForm>

        {/* <PopupWithForm name="delete-card" title="Вы уверены?" submit='Да' >
        </PopupWithForm> */}

        <Footer />
      </body>
    </div>
  );
}
export default App;
