// import logo from './logo.svg';
import '../index.css';
import './App.js';
import Header from './Header';
import Main from '../Main';
import Footer from './Footer';
import PopupWithForm from './PopupWithForm';
import React from 'react';

function App() {

  // const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false);

  function handleEditAvatarClick() {
    document.querySelector('.popup_profile-avatar')
    .classList.add("popup_opened");
    console.log('avatar')
 };
//  function handleEditProfileClick() {
//   setIsEditProfilePopupOpen.classList.add("popup_opened")
//   console.log('info')
// };
 function handleEditProfileClick() {
    document.querySelector('.popup_type_more-info')
    .classList.add("popup_opened");
    console.log('info')
 };
 function handleAddPlaceClick() {
    document.querySelector('.popup_type_more-item')
    .classList.add("popup_opened");
    console.log('item-add')
 }
  return (
    <div>
      <body className="page">
      <Header />
       <Main
       onEditProfile={handleEditProfileClick}
      //  onAddPlace={handleAddPlaceClick}
      //  onEditAvatar={handleEditAvatarClick}
       />
       <PopupWithForm container='_info' name='more-info' title='Редактировать профиль' close='popup__close' submit='Сохранить' />
       {/* <PopupWithForm name='more-item' title='Новое место' close='popup__close' submit='Создать' /> */}
       {/* <PopupWithForm name='delete-card' title='Обновить аватар' close='popup__close' submit='Да' /> */}
       {/* <PopupWithForm name='profile-avatar' title='Вы уверены?' close='popup__close' submit='Сохранить' /> */}
       <Footer />
      </body>
    </div>
  );
}
export default App;
